removeCharFromTitle1 <- function(string){
  return(gsub("(\\(.+\\))|(の.+)", "", string))
}
removeCharFromTitle2 <- function(string){
  return(gsub("(情報)|(番号)", "", string))
}
makeIndexName <- function(dfCol){
    retCol1 = removeCharFromTitle1(dfCol[1])
  if (dfCol[2] != "") {
    retCol1 = paste(retCol1,removeCharFromTitle2(dfCol[2]),sep="_")
  }
  return(retCol1)
}
colTitle <- apply(f,2,makeIndexName)
